#!/usr/bin/env python

N = (-1,0)
S = (1,0)
W = (0,-1)
E = (0,1)

def main(s):

  route = [x for x in s.split('\n') if x.strip()]
  (y,x) = (0,0)
  d = S
  steps = 0

  while route[y][x]==' ':
    x += 1

  try:
    while route[y][x]!=' ':
      steps += 1
      current = route[y][x]
      if current=='+':
        d = get_dir(route,x,y,d)
      (y,x) = map(sum,zip((y,x),d))
  except IndexError:
    pass

  return steps

def get_dir(route,x,y,last):

  last = (-last[0],-last[1])
  for d in (N,S,W,E):
    if d!=last:
      try:
        (y2,x2) = map(sum,zip((y,x),d))
        if route[y2][x2]!=' ':
          return d
      except IndexError:
        pass
  raise ValueError('Invalid route at r%s,c%s' % (y,x))

